signalapi.readyForSignal("Scale_Nodes", scaleVariables) variables.put("SIGNAL_ACTION","")
} else if (variables.get("SIGNAL_ACTION") == "SUBMIT_APPLICATION"){ signalapi.removeSignal("Submit_Application")
