signalapi.readyForSignal("Submit_Application", submitVariables) variables.put("SIGNAL_ACTION","") }

def action = variables.get("SIGNAL_ACTION") if (action?.trim()) {
called_workflow = variables.get("SUBMIT_APPLICATION_WORKFLOW") workflow_variables = new HashMap<>() workflow_variables.put("SPARK_APPLICATION_URL",variables.get("SPARK_APPLICATION_URL")) workflow_variables.put("SUBMISSION_PARAMETERS", variables.get("SUBMISSION_PARAMETERS")) workflow_variables.put("APPLICATION_ARGUMENTS", variables.get("APPLICATION_ARGUMENTS")) workflow_variables.put("SPARK_HEAD_NODE_ACCESS_TOKEN",variables.get("CLUSTER_NAME")+"-hn")
// connect to the scheduler schedulerapi.connect()
println "Submitting workflow " + called_workflow
