if (debug) {
    println "-------------- RESPONSE -----------------"
	println response.statusLine()
    println response.prettyPrint()
} else {
	println response.statusLine()
}
if (response.statusCode==HttpStatus.SC_NOT_FOUND) {
    variables.put("CLUSTER_STATE","NOT_FOUND")
} else {
response = response.then().assertThat()
