List successful_catalog_objects_staging = []
List failed_catalog_objects_staging = []
for (item in results) {
     Map.Entry<String,String> entry = (item.value() as Map).entrySet().iterator().next()
    if(entry.getValue() == "Promotion Successful"){
        successful_catalog_objects_staging.add(entry.getKey())
    } else {
        failed_catalog_objects_staging.add(entry.getKey())
    }
}
resultMap.put("Successful staging operations", successful_catalog_objects_staging)
resultMap.put("Failed staging operations", failed_catalog_objects_staging)
println "Successful catalog objects staging: " + successful_catalog_objects_staging
println "Failed catalog objects staging: " + failed_catalog_objects_staging
