filenames=[]
for x in range (variables.get("num_imgs")):
	filenames.append(str(variables.get('filename') + "_" + str(x) + ".txt"))




with open(variables.get('filename') + "_merged.txt", 'w') as outfile:
    for fname in filenames:
        with open(fname) as infile:
            for line in infile:
                outfile.write(line)


