import java.nio.file.Path import java.nio.file.Paths import java.nio.file.Files import java.nio.file.StandardCopyOption import groovy.io.FileType
sourceFilesFolder = new File(variables.get("SOURCE_FOLDER_PATH")) destinationFilesFolder = variables.get("DESTINATION_FOLDER_PATH") filePath = variables.get("DETECTED_FILE_NAME_AND_PATH") fileExist = variables.get("IF_FILE_EXISTS_AT_DESTINATION") println "**** FILE TRANSFER TO DESTINATION FOLDER ***"
try { file = new File(filePath) Path source = Paths.get(file.getAbsolutePath()) println("File source path: " + source.toString()) Path destination = Paths.get(destinationFilesFolder, file.getName()) println("File destination path: " + destination.toString()) if (fileExist == "Cancel" && new File(destination.toString()).exists()) { println "-- FILE TRANSFER CANCELLED -- the file " + file.getName() + " cannot be transferred as you have chosen to cancel the operation if it exists in the destination folder." return } Files.copy(source, destination, StandardCopyOption.REPLACE_EXISTING) println "SUCCESSFUL - file transfer from $source to $destination" } catch (IOException e) { println "FAILURE - file transfer from $source to $destination " + e.getMessage() }


import java.nio.file.Paths
import java.nio.file.Files

detectedFoundFile = variables.get("DELETE_DETECTED_FILE").toBoolean()
filePath = variables.get("DETECTED_FILE_NAME_AND_PATH")
if (detectedFoundFile) {
    boolean result = Files.deleteIfExists(Paths.get(filePath))
    if (result) {
        println("File " + filePath + " is deleted!")
    } else {
        println("Sorry, unable to delete the file.")
    }
}
