import org.ow2.proactive.scheduler.common.job.JobVariable import java.util.*;
if (variables.get("waitForSignals")){
List <JobVariable> signalVariables = new java.util.ArrayList<JobVariable>() signalVariables.add(new JobVariable("numberOfLights", "" + variables.get("numberOfLights"), "PA:LIST(1,2,3,4,5)", "number of lights used to calculate the coverage (must be between 1 and 5)", "", false, false)) signalVariables.add(new JobVariable("minRequiredAngle", "" + variables.get("minRequiredAngle"), "PA:INTEGER", "minimum total angle to cover", "", false, false)) signalVariables.add(new JobVariable("coveredAnglePerLight", "" + variables.get("coveredAnglePerLight"), "PA:INTEGER", "Angle covered by each light", "", false, false)) signalVariables.add(new JobVariable("NB_NODES", "" + variables.get("NB_NODES"), "PA:INTEGER", "Number of nodes to use in the calculation step", "Job Execution Parameters", false, false)) signalVariables.add(new JobVariable("TARGET_INFRASTRUCTURE", "" + variables.get("TARGET_INFRASTRUCTURE"), "PA:MODEL_FROM_URL(\${PA_SCHEDULER_REST_PUBLIC_URL}/rm/model/nodesources?infrastructure=%5E(%3F!(Default)).*\$)", "The node source where the job will be executed", "Job Execution Parameters", false, false)) signalVariables.add(new JobVariable("TOKEN", "" + variables.get("TOKEN"), "PA:MODEL_FROM_URL(\${PA_SCHEDULER_REST_PUBLIC_URL}/rm/model/tokens)", "(Optional) Please select a value in case the node source access is restricted with tokens", "Job Execution Parameters", false, false))
signalapi.readyForSignal("Terminate_Job") signalapi.readyForSignal("Rerun_Simulation", signalVariables)
variables.put("Terminate_Job", false) } variables.put("waitForSignals", false)
signals = new HashSet() signals.add("Terminate_Job") signals.add("Rerun_Simulation")
receivedSignal = signalapi.waitForAny(signals)
receivedSignalName = receivedSignal.getName() println "Received signal = " + receivedSignalName
if (receivedSignalName == "Terminate_Job") { variables.put("Terminate_Job", true) result = receivedSignalName } else { if (receivedSignalName == "Rerun_Simulation") { updatedVariables = receivedSignal.getUpdatedVariables() variables.put("numberOfLights", updatedVariables.get("numberOfLights")) variables.put("minRequiredAngle", updatedVariables.get("minRequiredAngle")) variables.put("coveredAnglePerLight", updatedVariables.get("coveredAnglePerLight")) variables.put("NB_NODES", updatedVariables.get("NB_NODES")) variables.put("NODE_SOURCE_INTERNAL", updatedVariables.get("TARGET_INFRASTRUCTURE")) variables.put("TOKEN_INTERNAL", updatedVariables.get("TOKEN")) signalapi.removeSignal("ready_Terminate_Job") variables.put("PA_TASK_ITERATION", 0) schedulerapi.connect() // Remove external endpoint schedulerapi.removeExternalEndpointUrl(variables.get("PA_JOB_ID"), "Simulation Results") result = receivedSignalName } else{ result = null } }
