print("BEGIN Train_Custering_Model")
from time import time import pandas as pd import numpy as np import pickle
IS_CLUSTERING_ALGORITHM = variables.get("CLUSTERING_ALGORITHM") DATA_TRAIN_DF_JSON = variables.get("DATA_TRAIN_DF_JSON")
if IS_CLUSTERING_ALGORITHM == 'True' and DATA_TRAIN_DF_JSON != None: ALGORITHM_NAME = variables.get("ALGORITHM_NAME") data_train_df = pd.read_json(DATA_TRAIN_DF_JSON, orient='split')
# CLUSTERING LEARNING if ALGORITHM_NAME == 'KMeans': from sklearn.cluster import KMeans n_clusters_para = variables.get("N_CLUSTERS_PARA") max_iter_para = variables.get("MAX_ITERATIONS_PARA") n_jobs_para = variables.get("N_JOBS_PARA") model = KMeans(n_clusters= int(n_clusters_para), max_iter= int(max_iter_para), n_jobs = int(n_jobs_para))
if ALGORITHM_NAME == 'MeanShift': from sklearn.cluster import MeanShift cluster_all_para = variables.get("CLUSTER_ALL_PARA") n_jobs_para = variables.get("N_JOBS_PARA") model = MeanShift(cluster_all = cluster_all_para, n_jobs = int(n_jobs_para))
model.fit(data_train_df.values) model_bin = pickle.dumps(model) variables.put("MODEL", model_bin)
print("END Train_Custering_Model")
else: print('Please check your ML pipeline')


