package org.ow2.proactive.cloud_watch.rules 
 import java.util.*;  
 import  org.ow2.proactive.cloud_watch.model.*; 
import  org.ow2.proactive.cloud_watch.service.*; 
global org.ow2.proactive.cloud_watch.action.ActionExecutor actionExecutorCloudAutomationService; 
declare isNodeAction 
 nodeMetrics: NodeMetrics 
end 

rule CPURule when
 exists   kpi : NodeMetrics( url == "service:jmx:rmi:///jndi/rmi://192.168.1.122:52304/rmnode" )
 $metric : NodeMetrics(metrics["sigar:Type=Cpu Total"]  > 1) 
 then 
    System.out.println("In then condition of cpu limit rule"); 
    Map<String, String> parameters = new HashMap(); 
 parameters.put("credentialsPath","/Users/admin/config/authentication/admin_user.cred"); 
    actionExecutorCloudAutomationService.execute(parameters, "CPURule", "{'bucket_name':'cloud-automation','workflow_name':'PCW_action_wf','variables':{}}"); 
end
